package lunes_02_05;

public class Ciclos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//mientras   while
		//for para
		int n = 10;
		for (int i = 0; i >= 0; i++) {  //i++ -->  i = i + 1
			System.out.println("i: "+i);
		}
		//gdfgfd
		
		//ciclo while
		n = 10;
		int i = 0;
		System.out.println("Ciclo While");
		while( (i < n) & (i!=7)){
			System.out.println("i: "+i); //loop infinito  bucle infinito
			i++;
		}
		
		
		
	}

}
